import { useEffect, useRef } from "react";
import { useScroll, useTransform, motion } from "framer-motion";
import Header from "./components/Header/Header";
import Hero from "./components/Hero/Hero";
import Lenis from "@studio-freight/lenis";
import TrustedBySection from "./components/TrustedBySection/TrustedBySection";
import Features from "./components/Features/Features";
import Footer from "./components/Footer/Footer";

export default function App() {
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const lenis = new Lenis({
      lerp: 0.1,
      wheelMultiplier: 1.2,
      touchMultiplier: 1.5,
    });

    const raf = (time: number) => {
      lenis.raf(time);
      requestAnimationFrame(raf);
    };

    requestAnimationFrame(raf);

    return () => lenis.destroy();
  }, []);

  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end start"],
  });

  const y = useTransform(scrollYProgress, [0, 1], [0, -100]);

  return (
    <div
      ref={containerRef}
      className="relative min-h-screen overflow-hidden bg-[#0B081C]"
    >
      <div className="fixed inset-0 -z-50 bg-gradient-to-br from-[#0c0523] via-[#1a0b2e] to-[#2d0b3a]" />

      <div className="fixed inset-0 -z-40 opacity-30 bg-[url('data:image/svg+xml;base64,')]" />

      <div className="relative z-10">
        <Header />

        <motion.div style={{ y }} className="mt-28 md:mt-36 lg:mt-44">
          <Hero />
        </motion.div>

        <TrustedBySection />
        <Features />
        <Footer />

      </div>
    </div>
  );
}
